import { PostMetadata } from "@/utils/mdx";
import {
  Container,
  ContentWrapper,
  Description,
  StyledImage,
  Tag,
  TagsContainer,
  TagsWrapper,
  ThumbnailWrapper,
  Title,
  Date
} from "./PostCard.styles";

interface PostCardProps {
  post: PostMetadata;
}

export default function PostCard({post}: PostCardProps) {
  return (
    <Container href={`/${post.category}/${post.slug}`}>
      <ThumbnailWrapper>
        <StyledImage 
          src={post.thumbnail ? post.thumbnail : ''} 
          alt='' 
          placeholder="blur"
          blurDataURL="data:image/jpeg;base64,"
          fill
        />
      </ThumbnailWrapper>
      <ContentWrapper>
        <Title>{post.title}</Title>
        <Description>{post.description}
        </Description>
        <TagsWrapper>
          <TagsContainer>
            {post.tags?.map((tag, index) => (
              <Tag key={index}>{tag}</Tag>
            ))}
          </TagsContainer>
          <Date>{post.createdAt}</Date>
        </TagsWrapper>
      </ContentWrapper>
    </Container>
  )
}